import React, { Component ,} from 'react'
import {Card, Input, Button, Table} from 'antd';
import { LineChart, Line ,XAxis,CartesianGrid,Tooltip,Legend,YAxis} from 'recharts';
import 'antd/dist/antd.css';
import {  compile } from 'mathjs';
import axios from 'axios';

const InputColor = {
    background: "",
    color: "#003a8c", 
    fontWeight: "bold", 
    fontSize: "24px",
    width: 300 ,
    height:50
    

};
var schedule = []
const table = [
    {
      title: "Iteration",
      Idatandex: "iteration",
      key: "iteration"
    },
    {
      title: "XL",
      dataIndex: "xl",
      key: "xl"
    },
    {
      title: "XR",
      dataIndex: "xr",
      key: "xr"
    },
    {
        title: "X",
        dataIndex: "x",
        key: "x"
    },
    {
      title: "Error",
      dataIndex: "error",
      key: "error"
      
    }
  ];

class FalsePosition extends Component {
    
    constructor() {
        super();
        this.state = {
            items: [],
            xl: 0,
            xr: 0,
            fx: "",
            showtable: false,
            showgraph: false
        }
        this.handleChange = this.handleChange.bind(this);
        this.false_position = this.false_position.bind(this);
    }
    

    false_position( xl, xr) {
        
        var xi = 0;
        var Epsilon= parseFloat(0.000000);
        var n=0;
        var array2d  = []
        array2d[0] = []//XL
        array2d[1] = []//XR
        array2d[2] = []//X
        array2d[3] = []//ERROR
       
        do{ 
            xi = (xl*this.func(xr) - xr*this.func(xl))/(this.func(xr)-this.func(xl));
            if (this.func(xi)*this.func(xr) < 0) {
                Epsilon = Math.abs((xi-xr) / xi);
        
                xl = xi;
                
            } 
            else {
                Epsilon = Math.abs((xi-xr) / xi);
            
                xr = xi;  
            }   
            array2d[0][n] =  xl;
            array2d[1][n] =  xr;
            array2d[2][n] =  xi.toFixed(6);
            array2d[3][n] = Math.abs(Epsilon).toFixed(6);
            n++;  

        }while(Math.abs(Epsilon)>0.000001);

        
        
        for (var i=0 ; i<array2d[0].length ; i++) {
            schedule.push({
                iteration: i+1,
                xl: array2d[0][i],
                xr: array2d[1][i],
                x: array2d[2][i],
                error: array2d[3][i]
            });
        }
        this.setState({
            showtable: true,
            showgraph: true
        })

        
    }
    func(datastr) {
        
        var comfunc = compile(this.state.fx)
        let data = {x:parseFloat(datastr)}

        return comfunc.evaluate(data)       
    }
    
    
    handleChange(event) {
        this.setState({
            [event.target.name]: event.target.value
        });
    }
    dataapi = async()=>{
        var response = await axios.get('http://localhost:3000/false-position').then(res => {return res.data});
        this.setState({
            fx:response['fx'],
            xl:response['xl'],
            xr:response['xr']
        })
        this.false_position(this.state.xl,this.state.xr);
    }
    render() {
        return(
            <body style={{ background: "#ebe18d", padding: "90px" , float:"left" }}>
                <h2 style={{color: "#003a8c", fontWeight: "bold",fontSize: "35px",textAlign:"center"}}>FalsePosition</h2>
                <div style={{textAlign:"center"}}>
                    <Card
                    
                    bordered={true}
                    style={{ width: 1500 ,height:600, background: "#ebe18d", float:"Auto" ,margin:"auto"}}
                    onChange={this.handleChange}
                    >
                        <h2>f(x)</h2><Input size="large" name="fx"   style={InputColor}></Input><br/><br/><br/><br/>
                        <h2>X<sub>L</sub></h2><Input size="large" name="xl" style={InputColor}></Input><br/><br/><br/><br/>
                        <h2>X<sub>R</sub></h2><Input size="large" name="xr" style={InputColor}></Input><br/><br/><br/><br/>
                        <Button id="submit_button" onClick= {
                                ()=>this.false_position(parseFloat(this.state.xl), parseFloat(this.state.xr))
                            }  
                            style={{width: 100 , height:50,background: "#003a8c", color: "white", fontSize: "25px"}}>GO</Button>&nbsp;&nbsp;&nbsp;&nbsp;

                        <Button id="submit_button" onClick= {
                                
                                ()=>this.dataapi()
                                
                                
                        }  
                        style={{width: 100 , height:50,background: "#003a8c", color: "white", fontSize: "25px"}}>API</Button>
                        
                    </Card>
                    
                    
                
                    {this.state.showtable && 
                        <Card
                        title={"Output"}
                        bordered={true}
                        style={{width: "100%", background: "#2196f3", color: "#FFFFFFFF", float:"inline-start", marginBlockStart:"2%"}}
                        id="outputCard"
                        >
                            <Table columns={table} bordered={true} dataSource={schedule} bodyStyle={{fontWeight: "bold", fontSize: "18px", color: "black"}}
                            ></Table>
                        </Card>
                    }
                    {this.state.showgraph &&
                        <LineChart width={730} height={250} data={schedule}
                        margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
                        <XAxis dataKey="error" />
                        <YAxis />
                        <CartesianGrid strokeDasharray="3 3" />
                        <Tooltip />
                        <Legend verticalAlign="top" height={36} />
                        <Line name="error" type="monotone" dataKey="error" stroke="#8884d8" />
                    </LineChart>                       
                    }    
                </div> 
                               
            

                
            </body>
        );
    }
}
export default FalsePosition;
